export class Node<T> {
  value: T;
  next: Node<T> | null;
  constructor(value: T, next?: Node<T> | null) {
    this.value = value;
    this.next = next === undefined ? null : next;
  }
}

export class LinkedList<T> implements ILinkedList<T> {
  private head: Node<T> | null;
  private tail: Node<T> | null;
  private size: number;
  constructor() {
    this.head = null;
    this.tail = null;
    this.size = 0;
  }

  append(element: T) {
    let node = new Node(element);
    if (this.size === 0) {
      this.head = node;
    } else {
      let current = this.head;
      while (current && current.next !== null) {
        current = current.next;
      }
      if (current) current.next = new Node(element);
    }
    this.size++;
  }

  prepend(element: T) {
    const newNode = new Node(element)

    if (!this.head || !this.tail) {
        this.head = newNode;
        this.tail = newNode;
        this.size++
        return this;
    };

    const currenNode = this.head;
    this.head = newNode;
    this.head.next = currenNode;
    this.size++
};

deleteHead() {
  if (!this.head) return null;

  if (this.size === 1) {
      this.head = null;
      this.tail = null;
      this.size = 0;
      return;
  };

  const currentHead = this.head;
  const newHead = currentHead.next;
  this.head = newHead;
  this.size--;
};

deleteTail() {
  if (!this.tail) return;

  if (this.size === 1) {
      this.head = null;
      this.tail = null;
      this.size = 0;
      return;
  };

  let current = this.head;
  let newTail = null;
  while (current) {
      if (current.next) {
          newTail = current;
      };
      current = current.next;
  };
  this.tail = newTail;
  this.tail!.next = null;
  this.size--;
};


  getSize() {
    return this.size;
  }

  insertAt(element: T, index: number) {
    if (index < 0 || index > this.size) {
      console.log("Enter a valid index");
      return;
    } else {
      const node = new Node(element);

      if (index === 0) {
        node.next = this.head;
        this.head = node;
        this.size++;
      } else {
        let curr = this.head;
        let currIndex = 0;
        for (let i = 0; i < index - 1; i++) {
          curr = curr!.next;
        }
        node.next = curr!.next;
        curr!.next = node;
        this.size++;
      }
    }
  }

  // print() {
