import React, { useState, ChangeEvent, useMemo } from "react";
import { TListItem, ILinkedList } from "../../types/list";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle";
import styles from "./list-page.module.css";
import { ElementStates } from "../../types/element-states";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { listenerCount } from "process";

export class Node<T> {
  value: T;
  next: Node<T> | null;
  constructor(value: T, next?: Node<T> | null) {
    this.value = value;
    this.next = next === undefined ? null : next;
  }
}

export class LinkedList<T> implements ILinkedList<T> {
  private head: Node<T> | null;
  private tail: Node<T> | null;
  private size: number;
  private appendArr(values: T[]) {
    values.forEach((value) => this.append(value));
};
  constructor(els: T[]) {
    this.head = null;
    this.tail = null;
    this.size = 0;
    if (els?.length) {
      this.appendArr(els);
  };
  }

  get(index: number) {
    if (index < 0 || index >= this.size) return null;
    let counter = 0;
    let curr = this.head;
    while (counter !== index && curr) {
      curr = curr?.next;
      counter++;
    }
    return curr;
  }

  getSize() {
    return this.size;
  }

  toArray() {
    let curr = this.head;
    const res = [];

    while (curr) {
        res.push(curr.value);
        curr = curr.next;
    };
    return [...res];
};

  append(element: T) {
    let node = new Node(element);
    if (this.size === 0) {
      this.head = node;
    } else {
      let current = this.head;
      while (current && current.next !== null) {
        current = current.next;
      }
      if (current) current.next = new Node(element);
    }
    this.size++;
  }

  prepend(element: T) {
    const newNode = new Node(element);

    if (!this.head || !this.tail) {
      this.head = newNode;
      this.tail = newNode;
      this.size++;
      return this;
    }

    const currenNode = this.head;
    this.head = newNode;
    this.head.next = currenNode;
    this.size++;
  }

  deleteHead() {
    if (!this.head) return null;

    if (this.size === 1) {
      this.head = null;
      this.tail = null;
      this.size = 0;
      return;
    }

    const currentHead = this.head;
    const newHead = currentHead.next;
    this.head = newHead;
    this.size--;
  }

  deleteTail() {
    if (!this.tail) return;

    if (this.size === 1) {
      this.head = null;
      this.tail = null;
      this.size = 0;
      return;
    }

    let current = this.head;
    let newTail = null;
    while (current) {
      if (current.next) {
        newTail = current;
      }
      current = current.next;
    }
    this.tail = newTail;
    this.tail!.next = null;
    this.size--;
  }

  addByIndex(element: T, index: number) {
    if (index < 0 || index > this.size) {
      console.log("Enter a valid index");
      return;
    } else {
      const node = new Node(element);

      if (index === 0) {
        node.next = this.head;
        this.head = node;
        this.size++;
      } else {
        let curr = this.head;
        let currIndex = 0;
        for (let i = 0; i < index - 1; i++) {
          curr = curr!.next;
        }
        node.next = curr!.next;
        curr!.next = node;
        this.size++;
      }
    }
  }

  removeByIndex(index: number) {
    if (index === 0) return this.deleteHead();
    if (index === this.size - 1) return this.deleteTail();
    let prev = this.get(index - 1);
    if (prev?.next) {
      let deletedNode = prev?.next;
      prev.next = deletedNode?.next;
      this.size--;
      return deletedNode;
    }
  }

  getArrWithColor() {
    return this.toArray().map(item => ({ value: item, color: ElementStates.Default }));
};
}

export const ListPage: React.FC = () => {
  const [inputValue, setInputValue] = useState("");
  const [indexValue, setIndexValue] = useState("");

  const getRandomNum = (min: number, max: number): number => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  const randomnum = getRandomNum(0, 99);
  const list = useMemo(() => new LinkedList<string>(Array.from({ length: 4 }, () => (randomnum.toString()))), []);


  const delay = (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value);
  };

  const onIndexChange = (e: ChangeEvent<HTMLInputElement>) => {
    setIndexValue(e.target.value);
  };

  const handleAddToHead = async () => {
    if (inputValue) {
      setInputValue("");
      list.append(inputValue);
    }
  };

  const handleAddToTail = () => {};

  const handleRemoveFromHead = () => {};

  const handleRemoveFromTail = () => {};

  const handleAddByIndex = () => {};

  const handleRemoveByIndex = () => {};

  const removeElements = (
    head: Node<number> | null,
    val: number
  ): Node<number> | null => {
    if (head === null) {
      return null;
    }

    let dummyHead = new Node(0);
    dummyHead.next = head;
    head = dummyHead;

    while (dummyHead.next !== null) {
      if (dummyHead.next.value === val) {
        dummyHead.next = dummyHead.next.next;
      } else {
        dummyHead = dummyHead.next;
      }
    }

    return head.next;
  };

  return (
    <SolutionLayout title="Связный список">
      <div className={styles.container}>
        <Input
          style={{ maxWidth: "204px" }}
          placeholder="Введите значение"
          maxLength={4}
          isLimitText={true}
          type="text"
          value={inputValue}
          onChange={onInputChange}
        />
        <Button style={{ minWidth: "175px" }} text="Добавить в head" />
        <Button style={{ minWidth: "175px" }} text="Добавить в tail" />
        <Button style={{ minWidth: "175px" }} text="Удалить из head" />
        <Button style={{ minWidth: "175px" }} text="Удалить из tail" />
      </div>
      <div className={styles.container}>
        <Input
          style={{ maxWidth: "204px" }}
          placeholder="Введите индекс"
          type="text"
          value={indexValue}
          onChange={onIndexChange}
        />
        <Button style={{ minWidth: "362px" }} text="Добавить по индексу" />
        <Button style={{ minWidth: "362px" }} text="Удалить по индексу" />
      </div>
      <ul className={styles.circles}>
        {/* {arr &&
          arr.map((item, index) => (
            <li key={index}>
              <Circle
                letter={item.value}
                state={item.color}
                index={index}
                head={addIndex(index, arr)}
              />
            </li>
          ))} */}
      </ul>
    </SolutionLayout>
  );
};
