    this.head = null;
    this.tail = null;
    this.size = 0;
    if (els?.length) {
      this.appendArr(els);
    }
  }

  get(index: number) {
    if (index < 0 || index >= this.size) return null;
    let counter = 0;
    let curr = this.head;
    while (counter !== index && curr) {
      curr = curr?.next;
      counter++;
    }
    return curr;
  }

  getSize() {
    return this.size;
  }

  toArray() {
    let curr = this.head;
    const res = [];

    while (curr) {
      res.push(curr.value);
      curr = curr.next;
    }
    return [...res];
  }

  append(element: T) {
    let node = new Node(element);
    if (this.size === 0) {
      this.head = node;
    } else {
      let current = this.head;
      while (current && current.next !== null) {
        current = current.next;
      }
      if (current) current.next = new Node(element);
    }
    this.size++;
  }

