import React, {useState, ChangeEvent} from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle";
import styles from './string.module.css';
import { ElementStates } from "../../types/element-states";

export const StringComponent: React.FC = () => {
  const swap = (arr: TArray[], firstIndex: number, secondIndex: number) => {
    const temp = arr[firstIndex];
    arr[firstIndex] = arr[secondIndex];
    arr[secondIndex] = temp;
    return arr;
  };

  type TArray = {
    value: string;
    color: ElementStates;
  };

    const [inputValue, setInputValue] = useState('');
    const [stringArr, setStringArr] = useState<Array<TArray>>([]);
    const [loader, setLoader] = useState(false);

    const onChange = (e: ChangeEvent<HTMLInputElement>) => {
      setInputValue(e.currentTarget.value);
    };

    const reverse = async (arr: TArray[]) => {
      setLoader(true);
      const mid = Math.ceil(arr.length / 2);

      for (let i = 0; i < mid; i++) {
        let j = arr.length - 1 - i;

        if (i !== j) {
          arr[i].color = ElementStates.Changing;
          arr[j].color = ElementStates.Changing;
          setStringArr([...arr]);
          //await delay(DELAY_IN_MS);
        };

        swap(arr, i, j);

        arr[i].color = ElementStates.Modified;
        arr[j].color = ElementStates.Modified;

        setStringArr([...arr]);
      }
      setLoader(false);
    };

    const handleButton = () => {
      const newArr = inputValue.split('').map((value => ({ value, color: ElementStates.Default })));
      reverse(newArr);
    };


  return (
    <SolutionLayout title="Строка">
        <div className={styles.container}>
        <div className={styles.input}>
          <Input
            isLimitText={true}
            maxLength={11}
            value={inputValue}
            onChange={onChange}
          />
          <div className={styles.button}>
            <Button text="Развернуть" onClick={handleButton} isLoader={loader} disabled={inputValue === ''} />
          </div>
        </div>
        <ul className={styles.circlesBox}>
          {stringArr && stringArr.map((item, index) =>
            <li key={index}>
              <Circle letter={item.value} state={item.color} />
            </li>)}
        </ul>
      </div>
      {/* <div className={styles.container}>
        <Input isLimitText maxLength={11} onChange={(e) => onInputChange} />
        <Button text="Развернуть" disabled />
        {inputState.map(el => {
          <Circle letter={el} />
        })}
      </div> */}
    </SolutionLayout>
  )
}
