import React, { useState, ChangeEvent, useMemo } from "react";
import { TListItem, ILinkedList } from "../../types/list";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle";
import { ArrowIcon } from "../ui/icons/arrow-icon";
import styles from "./list-page.module.css";
import { ElementStates } from "../../types/element-states";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";

export class Node<T> {
  value: T;
  next: Node<T> | null;
  constructor(value: T, next?: Node<T> | null) {
    this.value = value;
    this.next = next === undefined ? null : next;
  }
}

export class LinkedList<T> implements ILinkedList<T> {
  private head: Node<T> | null;
  private tail: Node<T> | null;
  private size: number;
  private addValues(values: T[]) { values.forEach((value) => this.append(value))}
  constructor(elements: T[]) {
    this.head = null;
    this.tail = null;
    this.size = 0;
    if (elements?.length) { this.addValues(elements) }
  }

  getSize() { return this.size }

  getCurr(index: number) {
    if (index < 0 || index >= this.size) return null;
    let counter = 0;
    let curr = this.head;
    while (counter !== index && curr) {
        curr = curr?.next;
        counter++
    }
    return curr;
}

  toArray() {
    let curr = this.head;
    const res = [];

    while (curr) {
      res.push(curr.value);
      curr = curr.next;
    }
    return [...res];
  }

  removeHead() {
    if (!this.head) return null;

    if (this.size === 1) {
        this.head = null;
        this.tail = null;
        this.size = 0;
        return;
    };

    const currentHead = this.head;
    const newHead = currentHead.next;
    this.head = newHead;
    this.size--;
};

removeTail() {
    if (!this.tail) return;

    if (this.size === 1) {
        this.head = null;
        this.tail = null;
        this.size = 0;
        return;
    };

    let current = this.head;
    let newTail = null;
    while (current) {
        if (current.next) {
            newTail = current;
        };
        current = current.next;
    };
    this.tail = newTail;
    this.tail!.next = null;
    this.size--;
};

  append(element: T) {
    let node = new Node(element);
    if (this.size === 0) {
      this.head = node;
    } else {
      let current = this.head;
      while (current && current.next !== null) {
        current = current.next;
      }
      if (current) current.next = new Node(element);
    }
    this.size++;
  }

  prepend(element: T) {
    const newNode = new Node(element);
    const currentNode = this.head;
    this.head = newNode;
    this.head.next = currentNode;
    this.size++;
  }

  addByIndex(element: T, index: number) {
    const newNode = new Node(element)

    if (index === 0) {
        newNode.next = this.head;
        this.head = newNode;
        this.size++
    };

    let prev = this.getCurr(index - 1);
    if (prev?.next) {
        let temp = prev?.next;
        prev!.next = newNode;
        newNode!.next = temp;
        this.size++
    };
    return
};

removeByIndex(index: number) {
  if (index === 0) return this.removeHead();
  if (index === this.size - 1) return this.removeTail();
  let prev = this.getCurr(index - 1);
  if (prev?.next) {
      let deletedNode = prev?.next;
      prev.next = deletedNode?.next;
      this.size--
      return deletedNode;
  };
};

}

export const ListPage: React.FC = () => {
  const [inputValue, setInputValue] = useState("");
  const [indexValue, setIndexValue] = useState("");

  const getRandomNumber = (min: number, max: number): number => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  const list = useMemo(
    () =>
      new LinkedList<string>(
        Array.from({ length: 4 }, () => getRandomNumber(0, 99).toString())
      ),
    []
  );

  const [arr, setArr] = useState<TListItem[]>( 
    list.toArray().map((item) => ({ value: item, color: ElementStates.Default }))
  );
