import React, { useState, ChangeEvent } from "react";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle";
import { delay, swap } from "../../utils/functions";
import { TArray } from "../../types/string";
import { ElementStates } from "../../types/element-states";
import styles from "./string.module.css";

export const StringComponent: React.FC = () => {
  const [inputValue, setInputValue] = useState("");
  const [inputArr, setInputArr] = useState<Array<TArray>>([]);
  const [loader, setLoader] = useState(false);

  const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.currentTarget.value);
  };



  const reverse = async (arr: TArray[]) => {
    setLoader(true);
    const mid = Math.ceil(arr.length / 2);

    for (let i = 0; i < mid; i++) {
      let j = arr.length - 1 - i;

      if (i !== j) {
        arr[i].color = ElementStates.Changing;
        arr[j].color = ElementStates.Changing;
        setInputArr([...arr]);
        await delay(1000);
      }

      swap(arr, i, j);

      arr[i].color = ElementStates.Modified;
      arr[j].color = ElementStates.Modified;

      setInputArr([...arr]);
    }
    setLoader(false);
  };

  const handleReverse = () => {
    const newArr = inputValue
      .split("")
      .map((value) => ({ value, color: ElementStates.Default }));
    reverse(newArr);
  };

  return (
    <SolutionLayout title="Строка">
      <div className={styles.container}>
        <div className={styles.inputcontainer}>
          <Input
            value={inputValue}
            isLimitText={true}
            maxLength={11}
            onChange={onInputChange}
          />
          <div className={styles.button}>
            <Button
              text="Развернуть"
              isLoader={loader}
              disabled={inputValue === "" ? true : false}
              onClick={handleReverse}
            />
          </div>
        </div>
        <ul className={styles.circlecontainer}>
          {inputArr &&
            inputArr.map((item, index) => (
              <li key={index}>
                <Circle letter={item.value} state={item.color} />
              </li>
            ))}
        </ul>
      </div>
    </SolutionLayout>
  );
};
