import React, { useState, ChangeEvent} from "react";
import { TListItem, ILinkedList } from "../../types/list";
import { Input } from "../ui/input/input";
import { Button } from "../ui/button/button";
import { Circle } from "../ui/circle/circle";
import styles from "./list-page.module.css";
import { ElementStates } from "../../types/element-states";
import { SolutionLayout } from "../ui/solution-layout/solution-layout";

export class Node<T> {
  value: T
  next: Node<T> | null
  constructor(value: T, next?: Node<T> | null) {
    this.value = value;
    this.next = (next === undefined ? null : next);
  }
}

export class LinkedList<T> implements ILinkedList<T> {
  private head: Node<T> | null;
  private size: number;
  constructor() {
    this.head = null;
    this.size = 0;
  }

  append(element: T) {    
     let node = new Node(element);
    if (this.size === 0) {     
      this.head = node; 
    } else { 
      let current = this.head;
      while (current && current.next !== null) { 
        current = current.next;      
      }
      if (current) current.next = new Node(element);    }
    this.size++;  
   }

  getSize() {
    return this.size;
  }

  insertAt(element: T, index: number) {
    if (index < 0 || index > this.size) {
      console.log('Enter a valid index');
      return;
    } else {
      const node = new Node(element);

      if (index === 0) {
        node.next = this.head;
        this.head = node;
        this.size++;
      } else {
        let curr = this.head;
        let currIndex = 0;
        for (let i = 0; i < index - 1; i++) {
          curr = curr!.next;
        }
      node.next = curr!.next;
      curr!.next = node;
      this.size++;
      }
   }
  }

  // print() {
  //   let curr = this.head;
  //   let res = '';
  //   while (curr) {
  //     res += `${curr.value} `;
  //     curr = curr.next;
  //   }
  //   console.log(res);
  // }
}

export const ListPage: React.FC = () => {
  const [arr, setArr] = useState<any>([]);
  const [inputValue, setInputValue] = useState("");
  const [indexValue, setIndexValue] = useState("");

  const delay = (ms: number) => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    setInputValue(e.target.value);
  };

  const onIndexChange = (e: ChangeEvent<HTMLInputElement>) => {
    setIndexValue(e.target.value);
  };

  const removeElements = (head: Node<number> | null, val: number): Node<number> | null => {
    if (head === null) {
        return null;
    }

    let dummyHead = new Node(0);
    dummyHead.next = head;
    head = dummyHead;

    while (dummyHead.next !== null) {
        if (dummyHead.next.value === val) {
            dummyHead.next = dummyHead.next.next;
        } else { 
            dummyHead = dummyHead.next;
        }
    }

    return head.next;
};

  return (
    <SolutionLayout title="Связный список">
    <div className={styles.container}>
      <Input style={{ maxWidth: '204px'}} placeholder="Введите значение" maxLength={4} isLimitText={true} type="text" value={inputValue} onChange={onInputChange} />
      <Button style={{ width: '175px' }} text="Добавить в head" />
      <Button style={{ width: '175px' }} text="Добавить в tail" />
      <Button style={{ width: '175px' }} text="Удалить из head" />
      <Button style={{ width: '175px' }} text="Удалить из tail" />
    </div>
    <div className={styles.container}>
      <Input style={{ maxWidth: '204px'}} placeholder="Введите индекс" type="text" value={indexValue} onChange={onIndexChange} />
      <Button text="Добавить по индексу" />
      <Button text="Удалить по индексу" />
    </div>
    <ul className={styles.circles}>
        {/* {arr &&
          arr.map((item, index) => (
            <li key={index}>
              <Circle
                letter={item.value}
                state={item.color}
                index={index}
                head={addIndex(index, arr)}
              />
            </li>
          ))} */}
      </ul>
    </SolutionLayout>
  );
};
